import React,{Component} from "react";
import PropTypes from 'prop-types';
import "./galeriaFotos.css";
import Gallery from 'react-grid-gallery';


import Casa from "../../images/casas/01.jpg";
import Piscinas from "../../images/piscinas/02.jpg";
import Telhados from "../../images/telhados/03.jpg"


class Galeria extends Component {

    constructor(props) {
        super(props);
        this.state = {
            casas: false,
            piscinas: false,
            telhados: false,
            images: this.props.images
        };
    }

    onClickCasas = () => {
        const { casas } = this.state; 
        this.setState({
            casas: !casas
        })
    }

    onClickPiscinas = () => {
        const { piscinas } = this.state; 
        this.setState({
            piscinas: !piscinas
        })
    }

    onClickTelhados = () => {
        const { telhados } = this.state; 
        console.log(galeria)
        this.setState({
            telhados: !telhados
        })
    }

    render() {
        const { teste } = this.state;
        return (
            <div className="galeria-container-flex" >
                <img className="galeria-thumb" src={Casa} onClick={this.onClickCasas}/> 
                {this.state.galeria && <Gallery
                    isOpen={this.state.galeria}
                    images={this.state.images}
                    enableImageSelection={false}
                />}
                <img className="galeria-thumb" src={Piscinas} onClick={this.onClickPiscinas}/> 
                {this.state.galeria && <Gallery
                    isOpen={this.state.galeria}
                    images={this.state.images}
                    enableImageSelection={false}
                />}
                <img className="galeria-thumb" src={Telhados} onClick={this.onClickTelhados}/> 
                {this.state.galeria && <Gallery
                    isOpen={this.state.galeria}
                    images={this.state.images}
                    enableImageSelection={false}
                />}
            </div>
        );
    }

}

Galeria.propTypes = {
    images: PropTypes.arrayOf(
        PropTypes.shape({
            src: PropTypes.string.isRequired,
            thumbnail: PropTypes.string.isRequired,
            srcset: PropTypes.array,
            caption: PropTypes.string,
            thumbnailWidth: PropTypes.number.isRequired,
            thumbnailHeight: PropTypes.number.isRequired,
            isSelected: PropTypes.bool
        })
    ).isRequired
};

